INSERT INTO `mejoras` (`id`, `nombre`, `descripcion`, `costo`, `tipo`) VALUES
(1, 'Encapsulamiento', 'Es la técnica de ocultar la implementación interna de una clase, de modo que solo se puedan acceder y modificar sus datos a través de métodos específicos.', 200, 'Principios'),
(2, 'Clases', 'Es una plantilla o modelo que define las características y comportamientos de un tipo de objeto.', 1000, 'Principios'),
(3, 'Herencia', 'Mecanismo que permite a una clase (clase derivada o subclase) obtener los atributos y métodos de otra clase (clase base o superclase) para reutilizarlos y ampliar su funcionalidad.', 5000, 'Relaciones'),
(4, 'Agregación', 'Forma de asociar objetos donde un objeto "tiene" otro, pero la existencia del objeto "parte" no depende del objeto "total".', 10000, 'Relaciones'),
(5, 'Asociación', 'Relación entre clases que permite a instancias de una clase interactuar con instancias de otra clase.', 50000, 'Relaciones'),
(6, 'Bucles', 'Estructura de control que permite ejecutar un bloque de código repetidamente hasta que se cumpla una condición determinada.', 500, 'Control');
